import 'whatwg-fetch';

const xhr = new XMLHttpRequest();
const url = "http://localhost:3004/product";

xhr.open("GET", url);
xhr.onreadystatechange = someHandler;
xhr.send();

class HttpService{
    getProducts =() =>{
        fetch('http://localhost:3004/product')
        .then(response =>{
            console.log(response.json());
        })
    }    
}

export default HttpService;
